import multiprocessingimport timefrom fund_info import fund_infoclass task_exec(multiprocessing.Process):    def __init__(self, task_queue, result_queue):        multiprocessing.Process.__init__(self)        self.task_queue = task_queue        self.result_queue = result_queue    def run(self):        proc_name = self.name        while True:            next_task = self.task_queue.get()            if next_task is None:                # terminate flag                print ('%s: Exiting' % proc_name)                self.task_queue.task_done()                break            print ('%s: %s' % (proc_name, next_task))            answer = next_task() # __call__()            self.task_queue.task_done()            self.result_queue.put(answer)        return